import{_ as e,c as l,ab as a,o as r}from"./chunks/framework.DijO4o3_.js";const p=JSON.parse('{"title":"生命周期函数","description":"","frontmatter":{},"headers":[],"relativePath":"mp-weixin/1微信小程序/17生命周期.md","filePath":"mp-weixin/1微信小程序/17生命周期.md"}'),n={name:"mp-weixin/1微信小程序/17生命周期.md"};function d(i,t,o,f,s,c){return r(),l("div",null,t[0]||(t[0]=[a('<h1 id="生命周期函数" tabindex="-1">生命周期函数 <a class="header-anchor" href="#生命周期函数" aria-label="Permalink to &quot;生命周期函数&quot;">​</a></h1><h2 id="生命周期概念" tabindex="-1">生命周期概念 <a class="header-anchor" href="#生命周期概念" aria-label="Permalink to &quot;生命周期概念&quot;">​</a></h2><p>生命周期</p><ul><li><p>出生</p></li><li><p>死亡</p></li></ul><p>生命周期(回调)函数</p><ul><li>出生时自动执行的(回调)函数</li><li>死亡是自动执行的(回调)函数</li></ul><h2 id="小程序生命周期函数分类" tabindex="-1">小程序生命周期函数分类 <a class="header-anchor" href="#小程序生命周期函数分类" aria-label="Permalink to &quot;小程序生命周期函数分类&quot;">​</a></h2><ul><li><code>App</code> 生命周期函数，<code>全局</code>生命周期函数。</li><li><code>Page</code> 生命周期函数，<code>页面</code>生命周期函数。🚩</li><li><code>Component</code> 生命周期函数，<code>组件</code>生命周期函数。</li></ul><h2 id="app-全局生命周期函数" tabindex="-1"><code>App</code> 全局生命周期函数 <a class="header-anchor" href="#app-全局生命周期函数" aria-label="Permalink to &quot;`App` 全局生命周期函数&quot;">​</a></h2><p>必须在 <code>app.js</code> 中调用，必须调用且只能调用一次。不然会出现无法预期的后果。</p><table tabindex="0"><thead><tr><th style="text-align:left;">属性</th><th style="text-align:left;">类型</th><th style="text-align:left;">说明</th><th style="text-align:left;">备注</th></tr></thead><tbody><tr><td style="text-align:left;"><a href="https://developers.weixin.qq.com/miniprogram/dev/reference/api/App.html#onLaunch-Object-object" target="_blank" rel="noreferrer">onLaunch</a></td><td style="text-align:left;">function</td><td style="text-align:left;">生命周期回调——监听小程序初始化。</td><td style="text-align:left;">全局只触发一次</td></tr><tr><td style="text-align:left;"><a href="https://developers.weixin.qq.com/miniprogram/dev/reference/api/App.html#onShow-Object-object" target="_blank" rel="noreferrer">onShow</a></td><td style="text-align:left;">function</td><td style="text-align:left;">生命周期回调——监听小程序启动或切前台。</td><td style="text-align:left;">主要显示的时候触发</td></tr><tr><td style="text-align:left;"><a href="https://developers.weixin.qq.com/miniprogram/dev/reference/api/App.html#onHide" target="_blank" rel="noreferrer">onHide</a></td><td style="text-align:left;">function</td><td style="text-align:left;">生命周期回调——监听小程序切后台。</td><td style="text-align:left;">隐藏微信和隐藏小程序的时候触发</td></tr></tbody></table><blockquote><p>注意：小程序点击右上角按钮其实并不是直接把小程序关闭掉，其实还是会在后台运行，大概 2 ~ 5 分钟 都没进入小程序才会把小程序真正关闭掉。</p></blockquote><h2 id="page-页面生命周期函数" tabindex="-1"><code>Page</code> 页面生命周期函数 <a class="header-anchor" href="#page-页面生命周期函数" aria-label="Permalink to &quot;`Page` 页面生命周期函数&quot;">​</a></h2><p>注册小程序中的一个页面。</p><table tabindex="0"><thead><tr><th style="text-align:left;">属性</th><th style="text-align:left;">类型</th><th style="text-align:left;">说明</th><th style="text-align:left;">备注</th></tr></thead><tbody><tr><td style="text-align:left;"><a href="https://developers.weixin.qq.com/miniprogram/dev/reference/api/Page.html#onLoad-Object-query" target="_blank" rel="noreferrer">onLoad</a></td><td style="text-align:left;">function</td><td style="text-align:left;">生命周期回调—监听页面加载</td><td style="text-align:left;">大部分发送请求都写这里</td></tr><tr><td style="text-align:left;"><a href="https://developers.weixin.qq.com/miniprogram/dev/reference/api/Page.html#onShow" target="_blank" rel="noreferrer">onShow</a></td><td style="text-align:left;">function</td><td style="text-align:left;">生命周期回调—监听页面显示</td><td style="text-align:left;">购物车页，每次打开都用最新的数量</td></tr><tr><td style="text-align:left;"><a href="https://developers.weixin.qq.com/miniprogram/dev/reference/api/Page.html#onHide" target="_blank" rel="noreferrer">onHide</a></td><td style="text-align:left;">function</td><td style="text-align:left;">生命周期回调—监听页面隐藏</td><td style="text-align:left;"></td></tr><tr><td style="text-align:left;"><a href="https://developers.weixin.qq.com/miniprogram/dev/reference/api/Page.html#onUnload" target="_blank" rel="noreferrer">onUnload</a></td><td style="text-align:left;">function</td><td style="text-align:left;">生命周期回调—监听页面卸载</td><td style="text-align:left;">普通页跳转 <code>tabBar</code> 页会卸载普通页</td></tr><tr><td style="text-align:left;"><a href="https://developers.weixin.qq.com/miniprogram/dev/reference/api/Page.html#onReady" target="_blank" rel="noreferrer">onReady</a></td><td style="text-align:left;">function</td><td style="text-align:left;">生命周期回调—监听页面初次渲染完成</td><td style="text-align:left;">了解即可</td></tr></tbody></table><p>注意：<code>tabBar</code> 页加载后就一直在内存中，所以 <code>tabBar</code> 页<code>不会触发卸载</code>的生命周期函数。</p><h2 id="组件生命周期" tabindex="-1">组件生命周期 <a class="header-anchor" href="#组件生命周期" aria-label="Permalink to &quot;组件生命周期&quot;">​</a></h2><table tabindex="0"><thead><tr><th style="text-align:left;">属性</th><th style="text-align:left;">类型</th><th style="text-align:left;">说明</th></tr></thead><tbody><tr><td style="text-align:left;"><a href="https://developers.weixin.qq.com/miniprogram/dev/framework/custom-component/lifetimes.html" target="_blank" rel="noreferrer">created</a></td><td style="text-align:left;">function</td><td style="text-align:left;">在组件实例刚刚被创建时执行</td></tr><tr><td style="text-align:left;"><a href="https://developers.weixin.qq.com/miniprogram/dev/framework/custom-component/lifetimes.html" target="_blank" rel="noreferrer">attached</a></td><td style="text-align:left;">function</td><td style="text-align:left;">在组件实例进入页面节点树时执行</td></tr><tr><td style="text-align:left;"><a href="https://developers.weixin.qq.com/miniprogram/dev/framework/custom-component/lifetimes.html" target="_blank" rel="noreferrer">ready</a></td><td style="text-align:left;">function</td><td style="text-align:left;">在组件在视图层布局完成后执行</td></tr><tr><td style="text-align:left;"><a href="https://developers.weixin.qq.com/miniprogram/dev/framework/custom-component/lifetimes.html" target="_blank" rel="noreferrer">moved</a></td><td style="text-align:left;">function</td><td style="text-align:left;">在组件实例被移动到节点树另一个位置时执行</td></tr><tr><td style="text-align:left;"><a href="https://developers.weixin.qq.com/miniprogram/dev/framework/custom-component/lifetimes.html" target="_blank" rel="noreferrer">detached</a></td><td style="text-align:left;">function</td><td style="text-align:left;">在组件实例被从页面节点树移除时执行</td></tr><tr><td style="text-align:left;"><a href="https://developers.weixin.qq.com/miniprogram/dev/framework/custom-component/lifetimes.html" target="_blank" rel="noreferrer">error</a></td><td style="text-align:left;">function</td><td style="text-align:left;">每当组件方法抛出错误时执行</td></tr></tbody></table>',18)]))}const g=e(n,[["render",d]]);export{p as __pageData,g as default};
