import{_ as a,c as i,ab as e,o as r}from"./chunks/framework.DnBlaP3q.js";const S=JSON.parse('{"title":"TypeScript介绍","description":"","frontmatter":{},"headers":[],"relativePath":"typescript/index.md","filePath":"typescript/index.md"}'),l={name:"typescript/index.md"};function d(s,t,o,c,n,p){return r(),i("div",null,t[0]||(t[0]=[e('<h1 id="typescript介绍" tabindex="-1">TypeScript介绍 <a class="header-anchor" href="#typescript介绍" aria-label="Permalink to &quot;TypeScript介绍&quot;">​</a></h1><ul><li>TS是是由微软2012年开发的一款开源的编程语言</li><li>TypeScript 是 Javascript 的超集，遵循最新的 ES6、Es5 规范。TypeScript 扩展了 JavaScript 的语法</li><li>在js的基础上，为js添加了<strong>类型支持</strong></li></ul><h2 id="设计目标" tabindex="-1">设计目标 <a class="header-anchor" href="#设计目标" aria-label="Permalink to &quot;设计目标&quot;">​</a></h2><ul><li>遵循当前以及未来出现的ECMAScript规范</li><li>开发大型应用，可以编译程纯JavaScript,编译出来的JavaScript可以运行在浏览器上</li><li>成为跨平台的开发工具，TypeScript使用Apache作为开源协议，且能够再所有主流的操作系统上安装和执行</li></ul><h2 id="ts优势" tabindex="-1">TS优势 <a class="header-anchor" href="#ts优势" aria-label="Permalink to &quot;TS优势&quot;">​</a></h2><ul><li>更早的发现错误</li><li>任何位置都有<strong>代码提示</strong>，增加开发效率</li><li>类型系统提升了代码的可维护性，重构更容易</li><li>使用最新的ECMAScript语法，最新</li><li>ts类型推断机制，降低成本</li></ul><h2 id="ts劣势" tabindex="-1">TS劣势 <a class="header-anchor" href="#ts劣势" aria-label="Permalink to &quot;TS劣势&quot;">​</a></h2><ul><li>短期投入到工作可能增加开发成本</li><li>和有些库的结合不是很完美</li><li>学习需要成本，需要理解接口，泛型，类型等知识</li><li>集成到自动构建流程中需要额外的工作量</li></ul><h2 id="ts与js的区别" tabindex="-1">TS与JS的区别 <a class="header-anchor" href="#ts与js的区别" aria-label="Permalink to &quot;TS与JS的区别&quot;">​</a></h2><table tabindex="0"><thead><tr><th>JavaScript</th><th>TypeScript</th></tr></thead><tbody><tr><td>动态语言</td><td>具有静态语言的特点</td></tr><tr><td>编译性语言运行时报错</td><td>编译期间报错</td></tr><tr><td>弱类型语言，没有类型</td><td>强类型语言，类似java, C++等，定义时指明类型</td></tr><tr><td>不支持模块、接口、泛型</td><td>支持模块、接口、泛型</td></tr><tr><td>基本数据类型和引用数据类型</td><td>更多的基本数据类型和引用数据类型，如any, never, enum等</td></tr><tr><td>在浏览器中直接执行</td><td>编译为js后才能在浏览器进行执行</td></tr></tbody></table>',10)]))}const u=a(l,[["render",d]]);export{S as __pageData,u as default};
